import { uiActions } from './ui-slice';



export const fatchCartData = () => {
    return dispatch => {
        const fetchData = async () => {
            const response = await fetch(
                'https://react-http-931c3-default-rtdb.europe-west1.firebasedatabase.app/cart.json');
        };
    };
};

export const sendCartData = (cart) => {
    return async (dispatch) => {
        dispatch(
            uiActions.showNotification({
                status: 'pending',
                title: 'sending...',
                message: 'Sending cart data!'
            }));

        const sendRequest = async () => {
            const response = await fetch('https://react-http-931c3-default-rtdb.europe-west1.firebasedatabase.app/cart.json',
                {
                    method: 'PUT',
                    body: JSON.stringify(cart)
                });
            if (!response.ok) {
                throw new Error('Sending cart data failed!')
            };
        };
        try {
            await sendRequest();
            dispatch(
                uiActions.showNotification({
                    status: 'success',
                    title: 'Success!',
                    message: 'Sent cart data successfully!'
                }));

        } catch (error) {
            dispatch(
                uiActions.showNotification({
                    status: 'error',
                    title: 'Error!',
                    message: 'Sending card data failed!'
                }));
        }

    }
};
